import React, { Component } from 'react'
import { Card,CardContent,Typography,Box,styled,Grid } from '@mui/material'
import { Description } from '@mui/icons-material'

const Compnent = styled(Card)`
    box-shadow: 0 2px 5px 0 rgb(0 0 0 / 16%), 0 2px 10px 0 rgb(0 0 0 / 12%);
    margin-bottom: 20px;
`;

const Container = styled(CardContent) `
    display: flex;
    padding: 8px;
    padding-bottom: 4px !important;
`;

const Image = styled('img')({
    height: 268,
    width: '88%',
    borderRadius: 5,
    objectFit: 'cover'
});

const RightContainer = styled(Grid)(({ theme }) => ({
    margin: '5px 0px 0 -25px',
    display: 'flex',
    flexDirection: 'column',
    [theme.breakpoints.between('sm', 'lg')]: {
        padding: '0 5px'
    },
    [theme.breakpoints.down('sm')]: {
        margin: '5px 0'
    }
}));

const Title = styled(Typography)`
    font-weight: 300;
    color: #44444d;
    font-size: 22px;
    line-height: 27px;
`;
const Author = styled(Typography)`
    color: #808290;
    font-size: 12px;
    line-height: 22px;
`;

const Descrption = styled(Typography)`
    line-height: 22px;
    color: #44444d;
    margin-top: 5px;
    font-family: 'Roboto',sans-serif;
    font-weight: 300;
`;

const Short = styled('b')({
    color: '#44444d',
    fontFamily: "'Convergence', sans-serif"
})

const Publisher = styled(Typography)`
    font-size: 12px;
    margin-top: auto;
    margin-bottom: 10px;
    '& > *': {
        textDecoration: 'none',
        color: '#000',
        fontWeight: 900
    }
`;

function Article({article}) {
  return (
    <Compnent>
        <Container>
            <Grid container>
                <Grid lg={5} sm={5} xs={12} item><Image src={article.img}/></Grid>
                <RightContainer lg={7} md={7} sm={7} xs={12} item>
                <Title>{article.title}</Title>
                <Author>
                    <Short>short</Short>by {article.author}/{new Date(article.timestamp).toDateString()}
                </Author>
                <Description>{article.description}</Description>
                <Publisher>
                    read more at <a href={article.link} target='_blank'>{article.publisher}</a>
                </Publisher>
                </RightContainer>
                       
            </Grid>
        </Container>
    </Compnent>
  )
}

export default Article
